import React, { useState, Component } from 'react';
import api from '../services/api';
import './UploadTest.css'
import Cadastro from './Cadastro'

const image_url = '';

class UploadTest extends Component {

    constructor(props) {
        super(props);
        this.state = {
            file: null,
            url_foto: null
        };

        this.onChange = this.onChange.bind(this);
    }


    onChange(e) {
        this.setState({ file: e.target.files[0] });

        e.preventDefault();
        const formData = new FormData();
        formData.append('myImage', e.target.files[0]);
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };
        api.post("/uploadImage", formData, config)
            .then((response) => {
                this.setState({ url_foto: response.data.image_url });
                alert("The file is successfully uploaded");
                image_url = response.data.image_url;
                
                
                console.log(image_url)
                
            }).catch((error) => {
            });
    }

    render() {
        return (
            <div className="container-form-element">


                <label htmlFor="foto">Foto</label>
                <input className="foto" type="file" name="myImage" onChange={this.onChange} />


            </div>

        )
    }
}

export default UploadTest;
