import React, { useState, Component } from 'react';
import { Link } from 'react-router-dom';
import './Cadastro.css';
import api from '../services/api';
import Upload from './UploadTest';

class Cadastro extends Component({ history }) {

    constructor(props) {
        super(props);
        this.state = {
            nome: null,
            email: null,
            telefone: null,
            senha: null,
            confirmacaoSenha: null,
            endereco: null,
            profissao: null,
            descricao: null,
            url_foto: null
        }
    }






onChange(e) {


    e.preventDefault();
    const formData = new FormData();
    formData.append('myImage', e.target.files[0]);
    const config = {
        headers: {
            'content-type': 'multipart/form-data'
        }
    };
    const response = api.post("/uploadImage", formData, config).then((response) => {
        console.log(response);
        this.setState({ url_foto: response.data.file.path })
        console.log(url_foto)
    })
        .catch(function (error) {
            console.log(error);
        });





}



handleSubmit(e) {

    console.log(url_foto)


    api.post('/cadastrarProfissional', {
        nome: this.state.nome,
        email,
        senha,
        profissao,
        descricao,
        endereco,
        telefone,
        url_foto,
        avaliacoes_negativas: 0,
        avaliacoes_positivas: 0,
    })



    e.preventDefault();

    alert("Cadastro realizado com sucesso!")
    history.push('/login');
}

render() {
    return (
        <div className="container-principal">
            <div className="container-form">

                <form onSubmit={handleSubmit}>
                    <div className="container-form-element">
                        <label htmlFor="nome">Nome</label>
                        <input required value={nome} onChange={e => this.setState({ nome: e.target.value })} id="nome" type="text" placeholder="Digite seu nome"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="email">Email</label>
                        <input required value={email} onChange={e => this.setState({ email: e.target.value })} id="email" type="text" placeholder="Digite seu email"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="telefone">Telefone</label>
                        <input required value={telefone} onChange={e => this.setState({ telefone: e.target.value })} id="telefone" type="text" placeholder="Digite seu número de telefone"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="senha">Senha</label>
                        <input required value={senha} onChange={e => this.setState({ senha: e.target.value })} id="senha" type="password" placeholder="Crie uma senha"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="confirmacao-senha">Confirmação de senha</label>
                        <input required value={confirmacaoSenha} onChange={e => sthis.setState({ confirmacaoSenha: e.target.value })} id="confirmacao-senha" type="password" placeholder="Redigite sua senha"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="endereco">Endereço</label>
                        <input required value={endereco} onChange={e => this.setState({ endereco: e.target.value })} id="endereco" type="text" placeholder="Digite seu endereço"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="profissao">Profissão</label>
                        <input required value={profissao} onChange={e => this.setState({ profissao: e.target.value })} id="profissao" type="text" placeholder="Digite sua profissão"></input>
                    </div>

                    <div className="container-form-element descricao">
                        <label htmlFor="descricao">Descrição</label>
                        <input required value={descricao} onChange={e => this.setState({ descricao: e.target.value })} id="descricao" type="text" placeholder="Descreva o seu trabalho"></input>
                    </div>

                    <div className="container-form-element">
                        <label htmlFor="foto">Foto</label>
                        <input className="foto" type="file" name="myImage" onChange={e => onChange(e)} />
                    </div>

                    <p>Ao se registrar, você concorda com nossos <a href="/termos">Termos de Uso</a></p>
                    <p>Já tem uma conta? <Link to="/login"> Entre aqui</Link></p>


                    <button type="submit">Registrar</button>
                </form>

            </div>

        </div>
    );
}

}

export default Cadastro;
